import React from 'react'
import Chart from 'chart.js'
import { connect } from 'react-redux'
import styled from '@emotion/styled'


const GenderGraph = ({ portugal }) => {

    const canvas = document.getElementById('genderGraph');
    const context = canvas && canvas.getContext('2d');

    let infectedM = [];
    for (let i in portugal.infected_m) {
        const value = portugal.infected_m[i];
        infectedM.push(value ? value : 0);
    }

    console.log(infectedM[infectedM.length-1])

    let infectedF = [];
    for (let i in portugal.infected_f) {
        const value = portugal.infected_f[i];
        infectedF.push(value ? value : 0);
    }

    console.log(infectedF[infectedM.length-1])

    const graph = context && new Chart(context, {
        type: 'bar',
        data: {
            labels: ['Nr. of infected'],
            datasets: [
                {
                    label: ['# of Man'],
                    data: [infectedM[infectedM.length-1]],
                    backgroundColor: [
                        'rgba(54, 162, 235, 0.2)',
                    ],
                    borderColor: [
                        'rgba(54, 162, 235, 1)',
                    ],
                    borderWidth: 1
                },
                {
                        label: ['# of Woman'],
                        data: [infectedF[infectedF.length-1]],
                        backgroundColor: [
                            'rgba(255, 99, 132, 0.2)',
                        ],
                        borderColor: [
                            'rgba(255, 99, 132, 1)',
                        ],
                        borderWidth: 1
                }]
        },
        options: {
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero: true
                    }
                }]
            }
        }
    });

    return (
        <canvas id={'genderGraph'}/>
    );
}

const mapStateToProps = (state) => {
    return {
        portugal: state.info.portugal,
    }
}

export default connect(mapStateToProps)(GenderGraph)
