export const getMapInfo = (country) => {
    return (dispatch, getState) =>  {
        fetch(`https://disease.sh/v3/covid-19/countries/${country}?strict=true`)
            .then( res => res.json()
                .then( r => {
                    let state = getState();
                    state = r;
                    dispatch({type: 'GET_MAP_INFO'});
                }))
            .catch( err => {
                console.error(err);
                dispatch({type: 'GET_MAP_INFO_ERROR'});
            })
    }
}

export const loadWorldInfo = () => {
    return (dispatch, getState) => {
        console.log(getState())
        dispatch({type: 'GET_WORLD_INFO'});
    }
}
