import { GetCountryInfo, GetCountryInfoError, GetWorldInfo } from "../actionList";

export const getMapInfo = (country) => {
    return (dispatch, getState) =>  {
        fetch(`https://disease.sh/v3/covid-19/countries/${country}?strict=true`)
            .then( res => res.json()
                .then( r => {
                    dispatch({type: GetCountryInfo, data: r});
                }))
            .catch( err => {
                console.error(err);
                dispatch({type: GetCountryInfoError, data: err});
            })
    }
}

export const loadWorldInfo = () => {
    return (dispatch, getState) => {
        dispatch({type: GetWorldInfo, data: {}});
    }
}
