import {GetCountryInfo, GetCountryInfoError, GetWorldInfo, GetWorldInfoError} from "../actionList";

const initialData = {
    country: {},
    world: [{}],
    error: '',
}

export const mapReducer = (state = initialData, action) => {
    switch (action.type) {
        case GetCountryInfo:
            return {
                ...state,
                country: action.data,
                error: '',
            };
        case GetCountryInfoError:
            return {
                ...state,
                error: action.data,
            };
        case GetWorldInfo:
            // return {
            //     ...state,
            //     world: action.data,
            //     error: '',
            // };
            console.log('hey')
            return state;
        case GetWorldInfoError:
            return {
                ...state,
                error: action.data,
            }
        default:
            return state;
    }
}
