import React, { memo } from "react";
import { rounded } from '../functions/round'
import {
    ZoomableGroup,
    ComposableMap,
    Geographies,
    Geography
} from "react-simple-maps";
import Tooltip from "./Tooltip";
const geoUrl =
    "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";


const MapChart = ({ setTooltipContent }) => {

    const getNumberOfInfected = country => {
        fetch(`https://disease.sh/v3/covid-19/countries/${country}?strict=true`)
            .then(r => r.json().then(res => {
                setTooltipContent(
                    <div>
                        <h2>{country}</h2>
                        <h3>tests: </h3> {res.cases}
                    </div>
                );
            }))
            .catch(err => console.error(err));
    }

    // tests:           tests
    // confirmed:       cases
    // recovered:       recovered
    // deaths:          deaths
    // last udpate      updated (timestamp)


    return (
        <>
            <ComposableMap data-tip="" projectionConfig={{ scale: 200 }}>
                <ZoomableGroup center={[-7, 40]} zoom={4}>
                    <Geographies geography={geoUrl}>
                        {({ geographies }) =>
                            geographies.map(geo => (
                                <Geography
                                    key={geo.rsmKey}
                                    geography={geo}
                                    onClick={() => {
                                        const { NAME } = geo.properties;
                                        getNumberOfInfected(NAME);
                                    }}
                                    onMouseEnter={() => {
                                        const { NAME, POP_EST } = geo.properties;
                                        setTooltipContent(`${NAME} — ${rounded(POP_EST)}`);
                                    }}
                                    onMouseLeave={() => {
                                        setTooltipContent("");
                                    }}
                                    style={{
                                        default: {
                                            fill: "#D6D6DA",
                                            outline: "none"
                                        },
                                        hover: {
                                            fill: "#F53",
                                            outline: "none"
                                        },
                                        pressed: {
                                            fill: "#E42",
                                            outline: "none"
                                        }
                                    }}
                                />
                            ))
                        }
                    </Geographies>
                </ZoomableGroup>
            </ComposableMap>
        </>
    );
};

export default memo(MapChart);
