import React, { useRef } from 'react'
import styled from "@emotion/styled";
import Chart from 'chart.js'
import { connect } from 'react-redux'
import { rounded } from '../../functions/round'

const Container = styled.div`
    flex: 2;
    border: 1px solid #ccc;
    margin-top: 10px;
`

const Graph = ({ portugal }) => {
    // parse json object into array of tracked days
    let trackedDays = [];
    for (let i in portugal.days)
        trackedDays.push(portugal.days[i])

    // parse json object into array of infected
    let nrInfected = []; let infectedPoints = [];
    for (let i in portugal.infected) {
        nrInfected.push(portugal.infected[i])
        if (i % 2 === 0)
            infectedPoints.push('rgba(255, 99, 132, 1)');
    }

    // parse json object into array of recovered
    let nrRecovered = []; let recoveredPoints = [];
    for (let i in portugal.recovered) {
        nrRecovered.push(portugal.recovered[i])
        if (i % 2 === 0)
            recoveredPoints.push('rgba(41,138,58, 1)');
    }

    const element = document.getElementById('myChart');
    const context = element && element.getContext('2d');

    const myChart = context && new Chart(context, {
        type: 'line',
        data: {
            labels: trackedDays,
            datasets: [
                {
                    label: '# of infected',
                    data: nrInfected,
                    backgroundColor: [
                        'rgba(255, 99, 132, 0.2)',
                    ],
                    borderColor: infectedPoints,
                    borderWidth: 1
                },
                {
                    label: '# of recovered',
                    data: nrRecovered,
                    backgroundColor: [
                        'rgb(152,253,149, 0.5)',
                    ],
                    borderColor: recoveredPoints,
                    borderWidth: 1
                }
                ]
        },
        options: {
            title: {
                display: true,
                text: 'Portugal track',
            },
            scales: {
                yAxes: [{
                    ticks: {
                        beginAtZero: true
                    }
                }]
            }
        }
    });
    return (
        <Container>
            <canvas id={'myChart'} width={660} height={300}/>
        </Container>
    )
}

const mapStateToProps = (state) => {
    return {
        portugal: state.info.portugal,
    }
}

export default connect(mapStateToProps)(Graph)
