import { GetMapInfo, GetMapInfoError, GetWorldInfo } from "../actionList";

export const getMapInfo = (country) => {
    return (dispatch, getState) =>  {
        fetch(`https://disease.sh/v3/covid-19/countries/${country}?strict=true`)
            .then( res => res.json()
                .then( r => {
                    dispatch({type: GetMapInfo, data: r});
                }))
            .catch( err => {
                console.error(err);
                dispatch({type: GetMapInfoError});
            })
    }
}

export const loadWorldInfo = () => {
    return (dispatch, getState) => {
        console.log(getState())
        dispatch({type: GetWorldInfo});
    }
}
