import { GetCountryInfo, GetCountryInfoError, GetWorldInfo } from "../actionList";

const initialData = {
    country: {},
    world: [{}],
    error: '',
}

export const mapReducer = (state = initialData, action) => {
    switch (action.type) {
        case GetCountryInfo:
            return {
                ...state,
                country: action.data,
                error: '',
            };
        case GetCountryInfoError:
            return {
                ...state,
                error: action.data,
            };
        case GetWorldInfo:
            return {
                ...state,
                world: action.data,
                error: '',
            };
        default:
            return state;
    }
    return state;
}
