import {GetCountryInfoSuccess, GetCountryInfoError, GetWorldInfoSuccess, GetWorldInfoError} from "../actionList";

const initialData = {
    country: {},
    world: [{}],
    error: '',
}

export const mapReducer = (state = initialData, action) => {
    switch (action.type) {
        case GetCountryInfoSuccess:
            return {
                ...state,
                country: action.data,
                error: '',
            };
        case GetCountryInfoError:
            return {
                ...state,
                error: action.data,
            };
        case GetWorldInfoSuccess:
            return {
                ...state,
                world: action.data,
                error: '',
            };
            return state;
        case GetWorldInfoError:
            return {
                ...state,
                error: action.data,
            }
        default:
            return state;
    }
}
