import { GetCountryInfoSuccess, GetCountryInfoError, GetWorldInfoSuccess, GetWorldInfoError } from "../actionList";
import moment from 'moment';

export const getMapInfo = (country) => {
    return (dispatch, getState) =>  {
        fetch(`https://disease.sh/v3/covid-19/countries/${country}?strict=true`)
            .then( res => res.json()
                .then( r => {
                    // parse the api response to store just the used data
                    const country = {
                        updatedAt: moment(r.updated).fromNow(),
                        name: r.country,
                        flag: r.countryInfo.flag,
                        nrInfected: r.cases,
                        nrDeaths: r.deaths,
                        nrRecovered: r.recovered,
                        nrTests: r.tests,
                    }
                    dispatch({type: GetCountryInfoSuccess, data: country});
                }))
            .catch( err => {
                console.error(err);
                dispatch({type: GetCountryInfoError, data: err});
            })
    }
}

export const getWorldInfo = () => {
    return (dispatch, getState) => {
        fetch('https://disease.sh/v3/covid-19/countries')
            .then( res => res.json()
                .then( r =>{
                    dispatch({ type: GetWorldInfoSuccess, data: r})
                }))
            .catch( err => {
                dispatch({ type: GetWorldInfoError, data: err})
            })
    }
}
