import { GetCountryInfo, GetCountryInfoError, GetWorldInfo } from "../actionList";

const initialData = {
    country: {},
    world: [{}],
}

export const mapReducer = (state = initialData, action) => {
    switch (action.type) {
        case GetCountryInfo:
            return {
                ...state,
                countr: action.data,
            };
        case GetCountryInfoError:
            return state;
        case GetWorldInfo:
            return state;
        default:
            return state;
    }
    return state;
}
