import React, { memo, useEffect } from "react";
import { rounded } from '../../functions/round'
import { connect } from 'react-redux'
import {getGlobalInfo, getMapInfo, getWorldInfo} from "../../store/actions/infoActions";
import {
    ZoomableGroup,
    ComposableMap,
    Geographies,
    Geography
} from "react-simple-maps";
const geoUrl =
    "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";


const MapChart = ({ setTooltipContent, getInfo, country, getWorld }) => {

    useEffect( () => {
        getWorld();
    }, []);

    const getNumberOfInfected = country => {
        fetch(`https://disease.sh/v3/covid-19/countries/${country}?strict=true`)
            .then(r => r.json().then(res => {
                setTooltipContent(
                    <div>
                        <h3>{country}</h3>
                        <strong>Tests: </strong> {rounded(res.tests)}<br/>
                        <strong>Confirmed: </strong> {rounded(res.cases)}<br/>
                        <strong>Recovered: </strong> {rounded(res.recovered)}<br/>
                        <strong>Deaths: </strong> {rounded(res.deaths)}<br/>
                        <strong>Last updated: </strong> {res.updated}
                    </div>
                );
            }))
            .catch(err => console.error(err));
    }

    // tests:           tests
    // confirmed:       cases
    // recovered:       recovered
    // deaths:          deaths
    // last udpate      updated (timestamp)

    console.log('country: ', country);
    return (
        <>
            <ComposableMap className={'map'} data-tip="" projectionConfig={{ scale: 50 }} width={500} height={500}>
                <ZoomableGroup center={[0, 0]} zoom={5}>
                    <Geographies geography={geoUrl}>
                        {({ geographies }) =>
                            geographies.map(geo => (
                                <Geography
                                    key={geo.rsmKey}
                                    geography={geo}
                                    onClick={() => {
                                        const { NAME } = geo.properties;
                                        // getNumberOfInfected(NAME);
                                        getInfo(NAME);
                                    }}
                                    onMouseEnter={() => {
                                        const { NAME, POP_EST } = geo.properties;
                                        setTooltipContent(`${NAME} — ${rounded(POP_EST)}`);
                                    }}
                                    onMouseLeave={() => {
                                        setTooltipContent("");
                                    }}
                                    style={{
                                        default: {
                                            fill: "grey",
                                            outline: "none"
                                        },
                                        hover: {
                                            fill: "#F53",
                                            outline: "none"
                                        },
                                        pressed: {
                                            fill: "#E42",
                                            outline: "none"
                                        }
                                    }}
                                />
                            ))
                        }
                    </Geographies>
                </ZoomableGroup>
            </ComposableMap>
        </>
    );
};

const mapDispatchToProps = (dispatch) => {
    return {
        getInfo: (country) => dispatch(getMapInfo(country)),
        getWorld: () => dispatch(getWorldInfo()),
        getGlobal: () => dispatch(getGlobalInfo()),
    }
}

const mapStateToProps = (state) => {
    return {
        country: state.info.country,
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(memo(MapChart));
